data-extraction-rules.cloud-backup:
rules_version = '2';
service cloud.firestore {
match /databases/{database}/documents {
match /users/{userId} {
allow read: if request.auth != null && request.auth.uid == userId; // Allow authenticated user to read their own data
allow write: if false; // Deny all write operations (change to appropriate conditions)
}
// Add more rules for other collections and documents as needed
}
}
